import React, {useState,useEffect} from "react";
import { NavLink } from "react-router-dom";

export default function Navbar() {
  Window.onscroll=()=>{
    if(Window.pageYOffset>0){document.getElementbyId('navbar').style.backgroundColor}
   }

  return (
    <div id="navbar">
      <NavLink
        exact
        to="/"
        className={(navData) => (navData.isActive ? "active" : "navlink")}
      >
        Home
      </NavLink>
      <ul>
        <NavLink
          exact
          to="/login"
          className={(navData) => (navData.isActive ? "active" : "navlink")}
        >
          Login
        </NavLink>
        <NavLink
          exact
          to="/register"
          className={(navData) => (navData.isActive ? "active" : "navlink")}
        >
          Register
        </NavLink>
      </ul>
    </div>
  );
}
