import React,{useEffect, useState,useRef} from 'react';
import axios from'axios';
import {URL} from '../config';
import {pulp_fiction} from '../config';
import { Swiper, SwiperSlide } from "swiper/react";

import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import { Pagination, Navigation } from "swiper";


export default function Principal() {
  const [trends,setTrends]=useState([]);
  const[isReady,setIsReady]=useState(false);

  const findTrendings = async () => {
      let url = `${URL}/media2/trending`;
      try {
        const res2 = await axios.get(url);
        setTrends(res2.data.media.results);
      } catch (error) {}
    };
    useEffect(() => {
      findTrendings();
    }, []);
    useEffect(()=>{
      setIsReady(true);
      console.log(trends)
    },[trends])

    const slider =()=>{
      return <Swiper
      pagination={{
        type: "progressbar",
      }}
      navigation={true}
      modules={[Pagination, Navigation]}
      className="mySwiper"
    >
      {trends.map((value,idx)=>{
          return <h1 ><SwiperSlide idx={idx}>{value.title||value.name}</SwiperSlide> </h1>
      })}
      </Swiper>
    }
  return (
    <div className='Principal'>
        {isReady ? slider():null}
        <img src={pulp_fiction} alt='pulp fiction'></img>
    </div>
  )
}
