import React, { useState } from 'react';
import axios from 'axios';
import { URL } from '../config';
import Usernavbar from '../components/Usernavbar'
import Display from './Display';

const Movies = ({logout}) => {
  const [ error, setError ] = useState('');
  const [ isReady, setIsReady ] = useState(false);
  const [ loading, setLoading ] = useState(false);
  const [ media, setMedia ] = useState({title:''});

  const handleChange = (e) => setMedia({ ...media, title: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    findMovie(media.title);
  };
  const findMovie = async (title) => {
    let url = `${URL}/media/search`;
    try {
      const res = await axios.post(url,{title});
      let { Error,Type } = res.data.movie;
      if(Type!=='movie'){
        if (!Type) {
          setError(`Seems that your movie doesn't exist`);
          return setIsReady(true);
        }
         setError(`Seems that you're searching a ${Type}`);
         return setIsReady(true);
      };
      setMedia(res.data.movie);
      setIsReady(true);
      setError(Error);
      } catch (error) {
      setError(error.message);
      setIsReady(true);
    }
  };


  return (
    <div>
      <Usernavbar logout={logout} />
      <form onSubmit={handleSubmit}>
        <input onChange={handleChange} value={media.title} />
        <button>Submit</button>
      </form>
      {isReady ? !error ? (
        <div>
          <h1>Title:{media.title}</h1>
          <h1>Release year:{media.year}</h1>
          <Display media={media}/>
        </div>
      ) : (
        <h1>{error}</h1>
      ) : loading ? (
        <img src="https://www.organizedthemes.com/wp-content/plugins/remind-me/js/loading.gif" alt="loading" />
      ) : null}
    </div>
  );
};

export default Movies;
