//import {apikey} from '../config';
import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [error, setError] = useState('')
  const [state, setState] = useState({
    title:'',
    year:'',
    movie:''
  })

  const handleChange = (e) => setState({ ...state, movie: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    findMovie(state.movie);
  }

  const findMovie = movie => {
    let url = `https://omdbapi.com?t=${movie}&apikey=thewdb`;
    axios.get(url)
    .then((res)=>{
        let { Title, Year, Error } = res.data;
        debugger
        setState({ 
          title: Title, 
          year: Year, 
          movie: '', 
        })
    })
    .catch((error)=>{
      debugger
      setError(error.message);
    })
  }

  let { title, year, movie } = state;

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input 
          onChange={handleChange}
          value={movie}
        />
        <button>Submit</button>
      </form> 
      <div>
      { !error ?
        <div>
        <h1>Title: {title}</h1>
        <h1>Release year: {year}</h1> 
        </div>
        : <h1>{error}</h1>
      }         
      </div>
    </div>
  );
}

export default App;
